mapper.select:
SELECT

FROM t_wx_user_like a

WHERE a.id = #{id}

SELECT
	count(*) as count
FROM t_wx_user_like a



		AND a.openid = #{openid}


		AND a.type = #{type}


		AND a.bizid = #{bizid}

	and flag=#{flag}

		SELECT

		FROM
		(SELECT id from  t_wx_user_like t


				AND t.openid = #{openid}


				AND t.type = #{type}


				AND t.bizid = #{bizid}

			and flag=#{flag}

		 ) b inner join
		t_wx_user_like a  on b.id=a.id


                 ORDER BY createtime desc


     limit #{startIndex},#{pageSize}

		SELECT

		FROM (SELECT id from  t_wx_user_like t


				AND t.openid = #{openid}


				AND t.type = #{type}


				AND t.bizid = #{bizid}

			and flag=#{flag}

		) b  inner join t_wx_user_like a  on b.id=a.id


                 ORDER BY createtime desc

SELECT

FROM
t_wx_user_like a


		AND a.openid = #{openid}


		AND a.type = #{type}


		AND a.bizid = #{bizid}

	and flag=#{flag}

limit 1

mapper.update:
UPDATE t_wx_user_like SET
flag = -1
	WHERE id = #{id}
