mapper.select:
SELECT 

FROM t_wx_user a

WHERE a.id = #{id}

SELECT 
	count(0) as count
FROM t_wx_user a


	a.flag = #{flag}

		AND a.id = #{id}


		AND a.openId = #{openid}

SELECT 

FROM t_wx_user a


	a.flag = #{flag}

		AND a.id = #{id}


		AND a.openId = #{openid}




		ORDER BY a.updateTime desc


limit #{startIndex},#{pageSize}

SELECT 

FROM t_wx_user a


	a.flag = #{flag}

		AND a.id = #{id}


		AND a.openId = #{openid}




		ORDER BY a.updateTime desc

mapper.update:
